import React from 'react';

const BotCard = ({ bot, enlisted, onBotSelect, onEnlist, onRelease, onDelete }) => {
  const handleButtonClick = () => {
    if (enlisted) {
      if (onRelease) {
        onRelease(bot); // Pass the bot object to onRelease callback
      } else {
        onDelete(bot); // Pass the bot object to onDelete callback
      }
    } else {
      onEnlist(bot); // Pass the bot object to onEnlist callback
    }
  };

  return (
    <div className={`bot-card${enlisted ? ' enlisted' : ''}`}>
      <h2>{bot.name}</h2>
      <p>Class: {bot.class}</p>
      <p>Health: {bot.health}</p>
      <p>Damage: {bot.damage}</p>
      <p>Armor: {bot.armor}</p>
      <button onClick={handleButtonClick}>
        {enlisted ? (onRelease ? 'Release' : 'Delete') : 'Enlist'}
      </button>
      <button onClick={() => onBotSelect(bot)}>View Details</button>
    </div>
  );
};

export default BotCard;
